def power(value,power):    original=1    for i in range(power):        original=original*value    return originallooper=int(input())input=input()list=[]list=str.split(input)for i in range(len(list)):    list[i]=int(list[i])sum=0flag=0if(looper>=1 and looper<=10):    for i in range(len(list)):        if (list[i]>=0 and list[i]<=power(10,10)):            flag=0        else: flag=1if flag==0:    for i in range(len(list)):        sum = sum+float(list[i])    print(int(sum))