from decimal import *getcontext().prec += 6n=int(input())string = " "string=input()list=[]list=str.split(string)divisor = n*1.0for i in range(len(list)):    list[i]=int(list[i])zeroes=positives=negatives=0.0for i in range(n):    if(list[i]==0):        zeroes=zeroes+1    if(list[i]>0):        positives=positives+1    if(list[i]<0):        negatives = negatives+1print((Decimal(positives/divisor)))print((Decimal(negatives/divisor)))print((Decimal(zeroes/divisor)))