def bubble_sort(array):    for i in range(len(array)):        for j in range(len(array)):            if array[i]<array[j]:                temp = array[i]                array[i]=array[j]                array[j]=temp    #print(array)    return arraylooper = int(input())marks=[]mainlist=[]list2=[]markslist=[]dict={}if(looper>=2 and looper<=5):    for i in range(looper):        student_name=input()        physics_marks=float(input())        marks.append(physics_marks)        list2.append(student_name)        list2.append(float(physics_marks))        markslist.append(float(physics_marks))    mainlist.append(list2)    #dict['MARKS']=physics_markslist3 = sorted(set(markslist))list3 = bubble_sort(list3)second_lowest=list3[1]#print(second_lowest)"""for i in range(0,looper*2):        print(mainlist[0][i],i)"""namelist=[]for j in range(int(looper*2)):    if(j%2!=0):        #print(mainlist[0][j])        if(float(mainlist[0][j])==second_lowest):            namelist.append(mainlist[0][j-1])namelist.sort()for i in range(len(namelist)):    print(namelist[i])